import React, { Component } from "react";
import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import { Container, Avatar } from "@material-ui/core";
import BannerSlider from "../Components/BannerSlider";
import ProductView from "../Components/ProductView";
import HorizontalScroller from "../Components/HorizontalScroller";
import StripAdView from "../Components/StripAdView";
import GridView from "../Components/GridView";
import { loadCategories } from "../Components/Actions/categoryActions";
import { connect } from "react-redux";
import { createStore } from "redux";
import { Home, Title } from "@material-ui/icons";

export class HomeFragment extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 0,
    };
  }

  handleChange = (event, newValue) => {
    this.setState({
      value: newValue,
    });
  };

  componentDidMount() {
    if (this.props.categories === null) {
      this.props.loadCategories();
    }
  }

  render() {
    return (
      <Container maxWidth="md" fixed>
        <AppBar position="static" color="white">
          <Tabs
            value={this.state.value}
            onChange={this.handleChange}
            indicatorColor="primary"
            textColor="primary"
            variant="scrollable"
            scrollButtons="auto"
            aria-label="scrollable auto tabs example"
          >
            <Tab icon={<CategoryTab />} />
            <Tab icon={<CategoryTab />} />
            <Tab icon={<CategoryTab />} />
            <Tab icon={<CategoryTab />} />
            <Tab icon={<CategoryTab />} />
            <Tab icon={<CategoryTab />} />
            <Tab icon={<CategoryTab />} />
            <Tab icon={<CategoryTab />} />
            <Tab icon={<CategoryTab />} />
            <Tab icon={<CategoryTab />} />
            <Tab icon={<CategoryTab />} />
            <Tab icon={<CategoryTab />} />
            <Tab icon={<CategoryTab />} />
            <Tab icon={<CategoryTab />} />
          </Tabs>
        </AppBar>
        <BannerSlider Images={[{ image: "fsfdfs" }]} />
        <HorizontalScroller />
        <StripAdView />
        <GridView />
      </Container>
    );
  }
}

export const CategoryTab = ({ icon, title }) => {
  return (
    <Box>
      {icon ? (
        <Avatar alt="Remy Sharp" variant="square" src={icon} />
      ) : (
        <Home />
      )}
      <Typography variant="body2">{title}</Typography>
    </Box>
  );
};

const mapStateToProps = (state) => {
  return {
    categories: state.categories,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    loadCategories: () => dispatch(loadCategories()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(HomeFragment);
