import { firestore } from "../firebase";

export const loadCategories = () => {
  return (dispatch, getstate) => {
    firestore
      .collection("CATEGORIES")
      .get()
      .then((querySnapshot) => {
        console.log(querySnapshot);
      })
      .catch((error) => {
        console.log(error);
      });
  };
};
