import React, { Component } from "react";
import {
  Container,
  Box,
  Typography,
  TextField,
  CircularProgress,
  Button,
} from "@material-ui/core";
import logo from "../media/logo.png";

class login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
      show_progress: false,
    };

    this.handleChange = this.handleChange.bind();
    this.login = this.login.bind();
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  login = () => {
    let valid_data = true;

    if (this.state.email === "") {
      this.state.email_error = "Required!";
      valid_data = false;
    }
    if (this.state.password === "") {
      this.state.password_error = "Required!";
      valid_data = false;
    }
    this.setState({
      update: true,
    });

    if (valid_data) {
      ///login
    }
  };

  render() {
    return (
      <Container maxWidth="xs">
        <Box
          bgcolor="white"
          boxShadow="10"
          borderRadius="12px"
          textAlign="center"
          p="24px"
          mt="110px"
        >
          <img src={logo} height="50px" alt="50px" />
          <Typography variant="h5" color="textSecondary">
            ADMIN
          </Typography>
          <TextField
            label="Email"
            id="outlined-size-small"
            variant="outlined"
            fullWidth
            name="email"
            onChange={this.handleChange}
            color="secondary"
            margin="normal"
            size="small"
          />

          <TextField
            label="Password"
            id="outlined-size-small"
            type="password"
            variant="outlined"
            fullWidth
            name="password"
            onChange={this.handleChange}
            color="secondary"
            margin="normal"
            size="small"
          />
          <br />
          <br />
          {this.state.show_progress ? (
            <CircularProgress size={24} thickness={4} color="secondary" />
          ) : null}
          <br />
          <br />
          <Button
            disableElevation
            variant="contained"
            onClick={this.login}
            color="primary"
            fullWidth
          >
            LOGIN
          </Button>
        </Box>
      </Container>
    );
  }
}

export default login;
